pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IHedgeStrategy {

    event Reward(uint256 amount);

    event Stake(uint256 amount);
    event Unstake(uint256 amount, uint256 amountReceived);

    event SetHealthFactor(uint256 healthFactor);

    struct BalanceItem {
        address token;
        uint256 amountUSD;
        uint256 amount;
        bool    borrowed;
    }

    function stake(
        uint256 _amount // value for staking in asset
    ) external;

    function unstake(
        uint256 _amount, // minimum expected value for unstaking in asset
        address _to      // PortfolioManager
    ) external returns (uint256); // Real unstake value

    function netAssetValue() external view returns (uint256); // Return value in USDC - denominator 6

    function claimRewards(address _to) external returns (uint256); // Return received amount in USDC - denominator 6

    function balance() external ; // Balancing aave health factor

    function currentHealthFactor() external view returns (uint256); // Aave healthFactor

    function balances() external view returns (BalanceItem[] memory ); // Balancing aave health factor

    function setHealthFactor(uint256 newHealthFactor) external; // Set a new health factor
}
","keccak256":"0x0307a2b48070e3bce80380677aaf1164f18f1929bda969048b6a0a19283eb5eb","license":"GPL-3.0"},"contracts/libraries/EtsCalculationLibrary.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;


import "hardhat/console.sol";

enum ActionType {
    ADD_LIQUIDITY, // D2, D6 < 0
    REMOVE_LIQUIDITY, // D2, D6 > 0

    SWAP_USDPLUS_TO_ASSET, // D1 < 0
    SWAP_ASSET_TO_USDPLUS, // D1 > 0

    SUPPLY_ASSET_TO_AAVE, // D4 > 0
    WITHDRAW_ASSET_FROM_AAVE, // D4 < 0

    BORROW_TOKEN_FROM_AAVE, // D5 > 0
    REPAY_TOKEN_TO_AAVE, // D5 < 0

    SWAP_TOKEN_TO_ASSET, // D3 < 0
    SWAP_ASSET_TO_TOKEN  // D3 > 0
}


enum Method {
    NOTHING,
    STAKE,
    UNSTAKE
}

// Amounts in decimals specific token, all positive
struct Amounts {
    uint256 collateralAsset;
    uint256 borrowToken;
    uint256 poolToken;
    uint256 poolUsdPlus;
    uint256 freeUsdPlus;
    uint256 freeAsset;
    uint256 freeToken;
}

// liquidity in USD e6, all positive
struct Liquidity {
    int256 collateralAsset;
    int256 borrowToken;
    int256 poolToken;
    int256 poolUsdPlus;
    int256 freeUsdPlus;
    int256 freeAsset;
    int256 freeToken;
}

// liquidity deltas in USD e6, may contain zeroes and below zero
struct Deltas {
    int256 d1;
    int256 d2;
    int256 d3;
    int256 d4;
    int256 d5;
    int256 d6;
    uint256 code;
}

struct Action {
    ActionType actionType;
    uint256 amount;
    uint256 slippagePercent;
}

struct Action2 {
    uint256 actionType;
    uint256 amount;
    uint256 slippagePercent;
}

struct CalcContext {
    int256 K1; // in e18
    int256 K2; // in e18
    int256 amount; // amount in USD, below zero if UNSTAKE
    Liquidity liq; // in USD e6
    uint256 tokenAssetSlippagePercent;
    Deltas deltas; // in USD e6
}

struct CalcContext2 {
    int256 K1; // in e18
    int256 K2; // in e18
    int256 amount; // amount in USD, below zero if UNSTAKE
    Liquidity liq; // in USD e6
    uint256 tokenAssetSlippagePercent;
}


library EtsCalculationLibrary{

    uint256 public constant MAX_UINT_VALUE = type(uint256).max;

    /**
      * d1 = -d2 - Lam - LFu;
      */
    function calcD1(CalcContext memory ctx) internal pure {
        ctx.deltas.d1 = - ctx.deltas.d2 - ctx.amount - ctx.liq.freeUsdPlus;
    }

    /**
      * d2 = d6 * K2;
      */
    function calcD2(CalcContext memory ctx) internal pure {
        ctx.deltas.d2 = ctx.deltas.d6 * ctx.K2 / 1e18;
    }

    /**
      * d3 = -LFw - d5 - d6;
      */
    function calcD3(CalcContext memory ctx) internal pure {
        ctx.deltas.d3 = - ctx.liq.freeToken - ctx.deltas.d5 - ctx.deltas.d6;
    }

    /**
      * d4 = K1 * (LBw + d5) - LCu;
      */
    function calcD4(CalcContext memory ctx) internal pure {
        ctx.deltas.d4 = (ctx.liq.borrowToken + ctx.deltas.d5) * ctx.K1 / 1e18 - ctx.liq.collateralAsset;
    }

    /**
      * d5 = LPw - LBw - d6;
      */
    function calcD5(CalcContext memory ctx) internal pure {
        ctx.deltas.d5 = ctx.liq.poolToken - ctx.liq.borrowToken - ctx.deltas.d6;
    }

    /**
     * d6 = ((K1 - 1) * LPw + LBw - LCu - LFu - LFuc - LFw - Lam) / (K1 + K2);
     */
    function calcD6(CalcContext memory ctx) internal view {

        logSign("K1:", ctx.K1);
        logSign("K2:", ctx.K2);


        int256 a = (ctx.K1 - 1e18) * ctx.liq.poolToken / 1e18;
        logSign("a:", a);
        a = a + ctx.liq.borrowToken;
        logSign("a:", a);
        a = a - ctx.liq.collateralAsset;
        logSign("a:", a);
        a = a - ctx.liq.freeUsdPlus;
        logSign("a:", a);
        a = a - ctx.liq.freeAsset;
        logSign("a:", a);
        a = a - ctx.liq.freeToken;
        logSign("a:", a);
        a = a - ctx.amount;
        logSign("a:", a);

        int256 b = ctx.K1 + ctx.K2;
        logSign("b:", b);
        int256 d6 = 1e18 * a / b;

        ctx.deltas.d6 = d6;
    }

    function logSign(string memory msg, int256 value) internal view {
        if (value < 0) {
            console.log(msg, "-", toUint256(- value));
        } else {
            console.log(msg, toUint256(value));
        }
    }

    function abs(int256 value) internal pure returns (uint256){
        if (value < 0) {
            return toUint256(- value);
        } else {
            return toUint256(value);
        }
    }

    function toUint256(int256 value) internal pure returns (uint256) {
        require(value >= 0, "SafeCast: value must be positive");
        return uint256(value);
    }

    function toInt256(uint256 value) internal pure returns (int256) {
        // Note: Unsafe cast below is okay because `type(int256).max` is guaranteed to be positive
        require(value <= uint256(type(int256).max), "SafeCast: value doesn't fit in an int256");
        return int256(value);
    }


    /**
      * NAV = sum of all tokens liquidity minus borrows.
      * @return NAV in USD
      */
    function _netAssetValue(Liquidity memory liq) internal pure returns (int256){

        // add liquidity in free tokens
        int256 navUsd = liq.freeUsdPlus + liq.freeAsset + liq.freeToken;
        // add liquidity in pool
        navUsd = navUsd + liq.poolToken + liq.poolUsdPlus;
        // add liquidity in aave collateral minus borrow
        navUsd = navUsd + liq.collateralAsset - liq.borrowToken;

        return navUsd;
    }

    function test() public view returns (uint256) {
        return 0;
    }


    function liquidityToActions(CalcContext2 memory ctx2) public view returns (Action[] memory, uint256) {

        Deltas memory deltas = Deltas(0, 0, 0, 0, 0, 0, 0);
        CalcContext memory ctx = CalcContext(ctx2.K1, ctx2.K2, ctx2.amount, ctx2.liq, ctx2.tokenAssetSlippagePercent, deltas);

        //if it is unstake then increase unstake amount to awoid slippages on swap
        if (ctx.amount < 0) {
            //if decrease on tokenAssetSlippagePercent then newAmount became oldAmount
            ctx.amount = ctx.amount * 10000 / (10000 - toInt256(ctx.tokenAssetSlippagePercent));
        }

        // order specified, don't change
        calcD6(ctx);
        calcD2(ctx);
        calcD1(ctx);
        calcD5(ctx);
        calcD4(ctx);
        calcD3(ctx);
        ctx.deltas.code = (ctx.deltas.d1 >= 0 ? 1 : 0) + 2 * (ctx.deltas.d2 >= 0 ? 1 : 0) + 4 * (ctx.deltas.d3 >= 0 ? 1 : 0) + 8 * (ctx.deltas.d4 >= 0 ? 1 : 0) + 16 * (ctx.deltas.d5 >= 0 ? 1 : 0);


        console.log("-------- Deltas");
        logSign("d1:", ctx.deltas.d1);
        logSign("d2:", ctx.deltas.d2);
        logSign("d3:", ctx.deltas.d3);
        logSign("d4:", ctx.deltas.d4);
        logSign("d5:", ctx.deltas.d5);
        logSign("d6:", ctx.deltas.d6);
        console.log("code:", ctx.deltas.code);
        console.log("--------");


        Action[] memory actions = new Action[](6);
        uint index;

        deltas = ctx.deltas;


        if (deltas.d1 < 0 && deltas.d3 < 0 && deltas.d4 < 0
        ) {
            revert("D1-D3-D4 have same sign");
        }

        if (deltas.d3 > 0 && deltas.d5 > 0 && deltas.d6 > 0
        ) {
            revert("D3-D5-D6 have same sign");
        }

        if (deltas.d3 >= 0 && deltas.d4 < 0 && deltas.d5 < 0) {
            // first circle cases (4 cases)
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.WITHDRAW_ASSET_FROM_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action((deltas.d1 < 0) ? ActionType.SWAP_USDPLUS_TO_ASSET : ActionType.SWAP_ASSET_TO_USDPLUS, abs(deltas.d1), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_TOKEN, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, (ctx2.amount < 0) ? uint(-ctx2.amount) : MAX_UINT_VALUE, 0);
        } else if (deltas.d3 < 0 && deltas.d4 >= 0 && deltas.d5 >= 0) {
            // second circle cases (4 cases)
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d3), 0);
            actions[index++] = Action(ActionType.SWAP_TOKEN_TO_ASSET, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action((deltas.d1 < 0) ? ActionType.SWAP_USDPLUS_TO_ASSET : ActionType.SWAP_ASSET_TO_USDPLUS, abs(deltas.d1), 0);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, (ctx2.amount < 0) ? uint(-ctx2.amount) : MAX_UINT_VALUE, 0);
        } else if (deltas.d1 >= 0 && deltas.d2 >= 0 && deltas.d3 < 0) {
            // unstake without circle (3 cases: 00011,01011,10011)
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d6), 0);
            actions[index++] = Action((deltas.d5 < 0) ? ActionType.REPAY_TOKEN_TO_AAVE : ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.SWAP_TOKEN_TO_ASSET, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action((deltas.d4 < 0) ? ActionType.WITHDRAW_ASSET_FROM_AAVE : ActionType.SUPPLY_ASSET_TO_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS , MAX_UINT_VALUE, 0);
        } else if (deltas.d1 < 0 && deltas.d2 < 0 && deltas.d3 >= 0) {
            // stake without circle (3 cases: 01100,10100,11100)
            actions[index++] = Action(ActionType.SWAP_USDPLUS_TO_ASSET, abs(deltas.d1), 0);
            actions[index++] = Action((deltas.d4 < 0) ? ActionType.WITHDRAW_ASSET_FROM_AAVE : ActionType.SUPPLY_ASSET_TO_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_TOKEN, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action((deltas.d5 < 0) ? ActionType.REPAY_TOKEN_TO_AAVE : ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, MAX_UINT_VALUE, 0);
        } else if (deltas.d1 < 0 && deltas.d2 >= 0 && deltas.d4 >= 0 && deltas.d5 < 0) {
            // balance without circle (2 cases: 01010,01110)
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d6), 0);
            actions[index++] = Action(ActionType.SWAP_USDPLUS_TO_ASSET, abs(deltas.d1), 0);
            actions[index++] = Action((deltas.d3 < 0) ? ActionType.SWAP_TOKEN_TO_ASSET : ActionType.SWAP_ASSET_TO_TOKEN, abs(deltas.d3), ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, MAX_UINT_VALUE, 0);
        } else if (deltas.d1 >= 0 && deltas.d2 < 0 && deltas.d4 < 0 && deltas.d5 >= 0) {
            // balance without circle (2 cases: 10001,10101)
            actions[index++] = Action(ActionType.WITHDRAW_ASSET_FROM_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action((deltas.d3 < 0) ? ActionType.SWAP_TOKEN_TO_ASSET : ActionType.SWAP_ASSET_TO_TOKEN, abs(deltas.d3), ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, (ctx2.amount < 0) ? uint(-ctx2.amount) : MAX_UINT_VALUE, 0);
        } else if (deltas.d1 >= 0 && deltas.d2 < 0 && deltas.d3 >= 0 && deltas.d4 >= 0 && deltas.d5 >= 0) {
            // D1-D3-D4 have same sign case (11101)
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS, abs(deltas.d1), 0);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_TOKEN, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, MAX_UINT_VALUE, 0);
        } else if (deltas.d1 >= 0 && deltas.d2 >= 0 && deltas.d3 >= 0 && deltas.d4 >= 0 && deltas.d5 < 0) {
            // D1-D3-D4 have same sign case (01111)
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS, abs(deltas.d1), 0);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_TOKEN, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d6), 0);
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, abs(deltas.d5), 0);
        } else if (deltas.d1 >= 0 && deltas.d2 < 0 && deltas.d3 >= 0 && deltas.d4 >= 0 && deltas.d5 < 0) {
            // D1-D3-D4 have same sign case (01101)
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS, abs(deltas.d1), 0);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_TOKEN, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, MAX_UINT_VALUE, 0);
        } else if (deltas.d1 >= 0 && deltas.d2 < 0 && deltas.d3 < 0 && deltas.d4 < 0 && deltas.d5 < 0) {
            // D3-D5-D6 have same sign case (00001)
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.SWAP_TOKEN_TO_ASSET, abs(deltas.d3), ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.WITHDRAW_ASSET_FROM_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, MAX_UINT_VALUE, 0);
        } else if (deltas.d1 >= 0 && deltas.d2 < 0 && deltas.d3 < 0 && deltas.d4 >= 0 && deltas.d5 < 0) {
            // D3-D5-D6 have same sign case (01001)
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.SWAP_TOKEN_TO_ASSET, abs(deltas.d3), ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, MAX_UINT_VALUE, 0);
        } else if (deltas.d1 < 0 && deltas.d2 < 0 && deltas.d3 < 0 && deltas.d4 >= 0 && deltas.d5 < 0) {
            // D3-D5-D6 have same sign case (01001)
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.SWAP_TOKEN_TO_ASSET, abs(deltas.d3), ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.SWAP_USDPLUS_TO_ASSET, abs(deltas.d1), 0);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, MAX_UINT_VALUE, 0);
        } else {
            revert("non-existent case");
        }

        // reassemble array
        Action[] memory tmp = new Action[](index);
        for (uint j; j < index; j++) {
            tmp[j] = actions[j];
        }
        actions = tmp;

        console.log("--------- actions");
        for (uint j; j < actions.length; j++) {
            console.log(j, uint(actions[j].actionType), actions[j].amount);
        }
        console.log("---------");
        return (actions, ctx.deltas.code);
    }

}


","keccak256":"0xe41d1393092f9d678a3e1d549d4ac23aef81dc216785e78f7a810853822aa4f7","license":"MIT"},"contracts/libraries/OpUsdcLibrary.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@overnight-contracts/connectors/contracts/stuff/UniswapV3.sol";

import "../StrategyOpUsdc.sol";

import "hardhat/console.sol";

library OpUsdcLibrary {

    /**
     * ActionType: ADD_LIQUIDITY
     * Add liquidity to mesh pool:
     * [op, usdc] -> mesh lpToken
     */
    function _addLiquidity(StrategyOpUsdc self, uint256 delta) public {
        if (self.op().balanceOf(address(self)) == 0 || self.usdPlus().balanceOf(address(self)) == 0) {
            return;
        }
        uint256 usdcBalanceBefore = self.usdc().balanceOf(address(self));
        self.exchange().redeem(address(self.usdc()), self.usdPlus().balanceOf(address(self)) - (delta == self.MAX_UINT_VALUE() ? 0 : delta));
        uint256 usdcBalanceAfter = self.usdc().balanceOf(address(self));

        self.router().addLiquidity(
            address(self.op()),
            address(self.usdc()),
            false,
            self.op().balanceOf(address(self)),
            usdcBalanceAfter - usdcBalanceBefore,
            0,
            0,
            address(this),
            block.timestamp
        );

        uint256 lpTokenBalance = self.pair().balanceOf(address(this));
        self.gauge().deposit(lpTokenBalance, 0);
    }

    /**
     * ActionType: REMOVE_LIQUIDITY
     * Remove liquidity from mesh pool:
     * mesh lpToken -> [op, usdc]
     * @param delta - op amount in USD e6
     */
    function _removeLiquidity(StrategyOpUsdc self, uint256 delta) public returns (uint256, uint256) {
        // calc op tokens amount
        uint256 lpForUnstake = _getLpForUnstake(self, delta);
        self.gauge().withdraw(lpForUnstake);

        uint256 usdcBalanceBefore = self.usdc().balanceOf(address(self));

        (uint256 amountOp, uint256 amountUsdc) = self.router().removeLiquidity(
            address(self.op()),
            address(self.usdc()),
            false,
            lpForUnstake,
            0,
            0,
            address(self),
            block.timestamp
        );

        uint256 usdPlusBalanceBefore = self.usdPlus().balanceOf(address(self));

        self.exchange().buy(address(self.usdc()), self.usdc().balanceOf(address(self)) - usdcBalanceBefore);

        return (amountOp, self.usdPlus().balanceOf(address(self)) - usdPlusBalanceBefore);
    }

    function _getLpForUnstake(StrategyOpUsdc self, uint256 delta) internal view returns (uint256 lpForUnstake) {
        uint256 poolTokenDelta = self.control().usdToOp(delta);
        uint256 lpTokenBalance = self.gauge().balanceOf(address(this));
        if (lpTokenBalance > 0) {
            uint256 totalLpBalance = self.pair().totalSupply();
            (uint256 reserveOp,,) = self.pair().getReserves();
            uint256 opBalance = reserveOp * lpTokenBalance / totalLpBalance;
            lpForUnstake = poolTokenDelta * lpTokenBalance / opBalance;
            if (lpForUnstake > lpTokenBalance) {
                lpForUnstake = lpTokenBalance;
            }
        }
    }

    /**
     * ActionType: SWAP_USDPLUS_TO_ASSET
     * Swap on exchange
     * usdPlus -> usdc
     * @param delta - UsdPlus in USD e6
     */
    function _swapUsdPlusToUsdc(StrategyOpUsdc self, uint256 delta) public {
        uint256 redeemUsdPlusAmount = (delta == self.MAX_UINT_VALUE() || self.control().usdToUsdc(delta) > self.usdPlus().balanceOf(address(self)))
                ? self.usdPlus().balanceOf(address(self))
                : self.control().usdToUsdc(delta);
        if (redeemUsdPlusAmount == 0) {
            return;
        }
        self.exchange().redeem(address(self.usdc()), redeemUsdPlusAmount);
    }

    /**
     * ActionType: SWAP_ASSET_TO_USDPLUS
     * Swap on exchange
     * usdc -> usdPlus
     * @param delta - Usdc in USD e6
     */
    function _swapUsdcToUsdPlus(StrategyOpUsdc self, uint256 delta) public {
        uint256 buyUsdcAmount = (delta == self.MAX_UINT_VALUE() || self.control().usdToUsdc(delta) > self.usdc().balanceOf(address(self)))
                ? self.usdc().balanceOf(address(self))
                : self.control().usdToUsdc(delta);
        if (buyUsdcAmount == 0) {
            return;
        }
        self.exchange().buy(address(self.usdc()), buyUsdcAmount);
    }

    /**
     * ActionType: SUPPLY_ASSET_TO_AAVE
     * usdc -> (supply aave)
     * @param delta - Usdc in USD e6
     */
    function _supplyUsdcToAave(StrategyOpUsdc self, uint256 delta) public {
        uint256 supplyUsdcAmount = (delta == self.MAX_UINT_VALUE() || self.control().usdToUsdc(delta) > self.usdc().balanceOf(address(self)))
                ? self.usdc().balanceOf(address(self))
                : self.control().usdToUsdc(delta);
        if (supplyUsdcAmount == 0) {
            return;
        }
        // aave pool may be changed, so we need always approve
        self.usdc().approve(address(self.control().aavePool()), supplyUsdcAmount);
        self.control().aavePool().deposit(
            address(self.usdc()),
            supplyUsdcAmount,
            address(self),
            self.REFERRAL_CODE()
        );
    }

    /**
     * ActionType: WITHDRAW_ASSET_FROM_AAVE
     * (aave) -> usdc
     * @param delta - Usdc in USD e6
     */
    function _withdrawUsdcFromAave(StrategyOpUsdc self, uint256 delta) public {
        uint256 withdrawUsdcAmount = self.control().usdToUsdc(delta);
        self.control().aavePool().withdraw(
            address(self.usdc()),
            withdrawUsdcAmount,
            address(self)
        );
    }

    /**
     * ActionType: BORROW_TOKEN_FROM_AAVE
     * (borrow from aave) -> op
     * @param delta - Op in USD e6
     */
    function _borrowOpFromAave(StrategyOpUsdc self, uint256 delta) public {
        uint256 borrowOpAmount = self.control().usdToOp(delta);
        self.control().aavePool().borrow(
            address(self.op()),
            borrowOpAmount,
            self.INTEREST_RATE_MODE(),
            self.REFERRAL_CODE(),
            address(self)
        );
    }

    /**
     * ActionType: REPAY_TOKEN_TO_AAVE
     * op -> (back to aave)
     * @param delta - Op in USD e6
     */
    function _repayOpToAave(StrategyOpUsdc self, uint256 delta) public {
        uint256 repayOpAmount = (delta == self.MAX_UINT_VALUE() || self.control().usdToOp(delta) > self.op().balanceOf(address(self)))
                ? self.op().balanceOf(address(self))
                : self.control().usdToOp(delta);
        if (repayOpAmount == 0) {
            return;
        }
        // aave pool may be changed, so we need always approve
        self.op().approve(address(self.control().aavePool()), repayOpAmount);
        self.control().aavePool().repay(
            address(self.op()),
            repayOpAmount,
            self.INTEREST_RATE_MODE(),
            address(self)
        );
    }

    /**
     * ActionType: SWAP_TOKEN_TO_ASSET
     * Swap on UniswapV3
     * op -> usdc
     * @param delta - Op in USD e6
     */
    function _swapOpToUsdc(StrategyOpUsdc self, uint256 delta, uint256 slippagePercent) public {
        uint256 swapOpAmount = (delta == self.MAX_UINT_VALUE() || self.control().usdToOp(delta) > self.op().balanceOf(address(self)))
                ? self.op().balanceOf(address(self))
                : self.control().usdToOp(delta);
        if (swapOpAmount == 0) {
            return;
        }

        uint256 amountOutMin = self.control().usdToUsdc(self.control().opToUsd(swapOpAmount / 10000 * (10000 - slippagePercent)));
        if (amountOutMin == 0) {
            return;
        }

        // if amount < 100 op use singleSwap op -> usdc
        // else use multiSwap op -> weth -> usdc
        if (swapOpAmount < 1e20) {
            UniswapV3Library.singleSwap(
                self.uniswapV3Router(),
                address(self.op()),
                address(self.usdc()),
                self.poolFeeOpUsdc(),
                address(self),
                swapOpAmount,
                amountOutMin
            );
        } else {
            UniswapV3Library.multiSwap(
                self.uniswapV3Router(),
                address(self.op()),
                address(self.weth()),
                address(self.usdc()),
                self.poolFeeOpWeth(),
                self.poolFeeWethUsdc(),
                address(self),
                swapOpAmount,
                amountOutMin
            );
        }
    }

    /**
     * ActionType: SWAP_ASSET_TO_TOKEN
     * Swap on UniswapV3
     * usdc -> op
     * @param delta - Usdc in USD e6
     */
    function _swapUsdcToOp(StrategyOpUsdc self, uint256 delta, uint256 slippagePercent) public {
        uint256 swapUsdcAmount = (delta == self.MAX_UINT_VALUE() || self.control().usdToUsdc(delta) > self.usdc().balanceOf(address(self)))
                ? self.usdc().balanceOf(address(self))
                : self.control().usdToUsdc(delta);
        if (swapUsdcAmount == 0) {
            return;
        }

        uint256 amountOutMin = self.control().usdToOp(self.control().usdcToUsd(swapUsdcAmount / 10000 * (10000 - slippagePercent)));
        if (amountOutMin == 0) {
            return;
        }

        // if amount < 100 usdc use singleSwap usdc -> op
        // else use multiSwap usdc -> weth -> op
        if (swapUsdcAmount < 1e8) {
            UniswapV3Library.singleSwap(
                self.uniswapV3Router(),
                address(self.usdc()),
                address(self.op()),
                self.poolFeeOpUsdc(),
                address(self),
                swapUsdcAmount,
                amountOutMin
            );
        } else {
            UniswapV3Library.multiSwap(
                self.uniswapV3Router(),
                address(self.usdc()),
                address(self.weth()),
                address(self.op()),
                self.poolFeeWethUsdc(),
                self.poolFeeOpWeth(),
                address(self),
                swapUsdcAmount,
                amountOutMin
            );
        }
    }

