library BeethovenLibrary {


    function swap(
        IVault beethovenxVault,
        bytes32 poolId,
        IVault.SwapKind kind,
        IERC20 tokenIn,
        IERC20 tokenOut,
        address sender,
        address recipient,
        uint256 amount,
        uint256 limit
    ) internal returns (uint256) {

        tokenIn.approve(address(beethovenxVault), tokenIn.balanceOf(address(this)));

        IVault.SingleSwap memory singleSwap;
        singleSwap.poolId = poolId;
        singleSwap.kind = kind;
        singleSwap.assetIn = IAsset(address(tokenIn));
        singleSwap.assetOut = IAsset(address(tokenOut));
        singleSwap.amount = amount;

        IVault.FundManagement memory fundManagement;
        fundManagement.sender = sender;
        fundManagement.fromInternalBalance = false;
        fundManagement.recipient = payable(recipient);
        fundManagement.toInternalBalance = false;

        return beethovenxVault.swap(singleSwap, fundManagement, limit, block.timestamp + 600);
    }

    function batchSwap(
        IVault beethovenxVault,
        bytes32 poolId1,
        bytes32 poolId2,
        IVault.SwapKind kind,
        IAsset tokenIn,
        IAsset tokenMid,
        IAsset tokenOut,
        address sender,
        address payable recipient,
        uint256 amount
    ) internal returns (uint256) {

        IERC20(address(tokenIn)).approve(address(beethovenxVault), amount);

        IVault.BatchSwapStep[] memory swaps = new IVault.BatchSwapStep[](2);

        IVault.BatchSwapStep memory batchSwap1;
        batchSwap1.poolId = poolId1;
        batchSwap1.assetInIndex = 0;
        batchSwap1.assetOutIndex = 1;
        batchSwap1.amount = amount;
        swaps[0] = batchSwap1;

        IVault.BatchSwapStep memory batchSwap2;
        batchSwap2.poolId = poolId2;
        batchSwap2.assetInIndex = 1;
        batchSwap2.assetOutIndex = 2;
        batchSwap2.amount = 0;
        swaps[1] = batchSwap2;

        IAsset[] memory assets = new IAsset[](3);
        assets[0] = tokenIn;
        assets[1] = tokenMid;
        assets[2] = tokenOut;

        IVault.FundManagement memory fundManagement;
        fundManagement.sender = sender;
        fundManagement.fromInternalBalance = false;
        fundManagement.recipient = recipient;
        fundManagement.toInternalBalance = false;

        int256[] memory limits = new int256[](3);
        if (kind == IVault.SwapKind.GIVEN_IN) {
            limits[0] = 10 ** 27;
            limits[1] = 10 ** 27;
            limits[2] = 10 ** 27;
        } else {
            limits[0] = 0;
            limits[1] = 0;
            limits[2] = 0;
        }

        return uint256(- beethovenxVault.batchSwap(kind, swaps, assets, fundManagement, limits, block.timestamp + 600)[2]);
    }

    function onSwap(
        IVault beethovenxVault,
        bytes32 poolId,
        IVault.SwapKind kind,
        IERC20 tokenIn,
        IERC20 tokenOut,
        uint256 balance
    ) internal view returns (uint256) {

        IPoolSwapStructs.SwapRequest memory swapRequest;
        swapRequest.kind = kind;
        swapRequest.tokenIn = tokenIn;
        swapRequest.tokenOut = tokenOut;
        swapRequest.amount = balance;

        (IERC20[] memory tokens, uint256[] memory balances, ) = beethovenxVault.getPoolTokens(poolId);

        (address pool, IVault.PoolSpecialization poolSpecialization) = beethovenxVault.getPool(poolId);

        if (poolSpecialization == IVault.PoolSpecialization.GENERAL) {

            uint256 indexIn;
            uint256 indexOut;
            for (uint8 i = 0; i < tokens.length; i++) {
                if (tokens[i] == tokenIn) {
                    indexIn = i;
                } else if (tokens[i] == tokenOut) {
                    indexOut = i;
                }
            }

            return IGeneralPool(pool).onSwap(swapRequest, balances, indexIn, indexOut);

        } else if (poolSpecialization == IVault.PoolSpecialization.MINIMAL_SWAP_INFO) {

            uint256 balanceIn;
            uint256 balanceOut;
            for (uint8 i = 0; i < tokens.length; i++) {
                if (tokens[i] == tokenIn) {
                    balanceIn = balances[i];
                } else if (tokens[i] == tokenOut) {
                    balanceOut = balances[i];
                }
            }

            return IMinimalSwapInfoPool(pool).onSwap(swapRequest, balanceIn, balanceOut);

        } else {

            uint256 balanceIn;
            uint256 balanceOut;
            for (uint8 i = 0; i < tokens.length; i++) {
                if (tokens[i] == tokenIn) {
                    balanceIn = balances[i];
                } else if (tokens[i] == tokenOut) {
                    balanceOut = balances[i];
                }
            }

            return IMinimalSwapInfoPool(pool).onSwap(swapRequest, balanceIn, balanceOut);
        }
    }


    function queryBatchSwap(
        IVault vault,
        IVault.SwapKind kind,
        IERC20 token0,
        IERC20 token1,
        bytes32 poolId0,
        uint256 amount0,
        address sender,
        address recipient
    ) internal returns (uint256) {

        IVault.BatchSwapStep[] memory swaps = new IVault.BatchSwapStep[](1);
        swaps[0] = IVault.BatchSwapStep(poolId0, 0, 1, amount0, new bytes(0));

        IAsset[] memory assets = new IAsset[](2);
        assets[0] = IAsset(address(token0));
        assets[1] = IAsset(address(token1));

        IVault.FundManagement memory fundManagement = IVault.FundManagement(sender, false, payable(recipient), false);

        return uint256(- vault.queryBatchSwap(kind, swaps, assets, fundManagement)[1]);
    }


    struct SwapParams {
        IVault beethovenxVault;
        IVault.SwapKind kind;
        address token0;
        address token1;
        address token2;
        address token3;
        bytes32 poolId0;
        bytes32 poolId1;
        bytes32 poolId2;
        uint256 amount;
        address sender;
        address recipient;
    }

    struct CalculateParams {
        uint256 amount0Total;
        uint256 totalLpBalance;
        uint256 reserve0;
        uint256 reserve1;
        uint256 denominator0;
        uint256 denominator1;
        uint256 precision;
    }

    function queryBatchSwap(SwapParams memory swapParams) internal returns (uint256) {

        IVault.BatchSwapStep memory batchSwap0;
        batchSwap0.poolId = swapParams.poolId0;
        batchSwap0.assetInIndex = 0;
        batchSwap0.assetOutIndex = 1;
        batchSwap0.amount = swapParams.amount;

        IVault.BatchSwapStep memory batchSwap1;
        batchSwap1.poolId = swapParams.poolId1;
        batchSwap1.assetInIndex = 1;
        batchSwap1.assetOutIndex = 2;
        batchSwap1.amount = 0;

        IVault.BatchSwapStep memory batchSwap2;
        batchSwap2.poolId = swapParams.poolId2;
        batchSwap2.assetInIndex = 2;
        batchSwap2.assetOutIndex = 3;
        batchSwap2.amount = 0;

        IVault.BatchSwapStep[] memory swaps = new IVault.BatchSwapStep[](3);
        swaps[0] = batchSwap0;
        swaps[1] = batchSwap1;
        swaps[2] = batchSwap2;

        IAsset[] memory assets = new IAsset[](4);
        assets[0] = IAsset(swapParams.token0);
        assets[1] = IAsset(swapParams.token1);
        assets[2] = IAsset(swapParams.token2);
        assets[3] = IAsset(swapParams.token3);

        IVault.FundManagement memory fundManagement;
        fundManagement.sender = swapParams.sender;
        fundManagement.fromInternalBalance = false;
        fundManagement.recipient = payable(swapParams.recipient);
        fundManagement.toInternalBalance = false;

        return uint256(- swapParams.beethovenxVault.queryBatchSwap(swapParams.kind, swaps, assets, fundManagement)[3]);
    }

    function batchSwap(SwapParams memory swapParams) internal returns (uint256) {

        IERC20(swapParams.token0).approve(address(swapParams.beethovenxVault), swapParams.amount);

        IVault.BatchSwapStep memory batchSwap0;
        batchSwap0.poolId = swapParams.poolId0;
        batchSwap0.assetInIndex = 0;
        batchSwap0.assetOutIndex = 1;
        batchSwap0.amount = swapParams.amount;

        IVault.BatchSwapStep memory batchSwap1;
        batchSwap1.poolId = swapParams.poolId1;
        batchSwap1.assetInIndex = 1;
        batchSwap1.assetOutIndex = 2;
        batchSwap1.amount = 0;

        IVault.BatchSwapStep memory batchSwap2;
        batchSwap2.poolId = swapParams.poolId2;
        batchSwap2.assetInIndex = 2;
        batchSwap2.assetOutIndex = 3;
        batchSwap2.amount = 0;

        IVault.BatchSwapStep[] memory swaps = new IVault.BatchSwapStep[](3);
        swaps[0] = batchSwap0;
        swaps[1] = batchSwap1;
        swaps[2] = batchSwap2;

        IAsset[] memory assets = new IAsset[](4);
        assets[0] = IAsset(swapParams.token0);
        assets[1] = IAsset(swapParams.token1);
        assets[2] = IAsset(swapParams.token2);
        assets[3] = IAsset(swapParams.token3);

        IVault.FundManagement memory fundManagement;
        fundManagement.sender = swapParams.sender;
        fundManagement.fromInternalBalance = false;
        fundManagement.recipient = payable(swapParams.recipient);
        fundManagement.toInternalBalance = false;

        int256[] memory limits = new int256[](4);
        limits[0] = 1e27;

        return uint256(- swapParams.beethovenxVault.batchSwap(swapParams.kind, swaps, assets, fundManagement, limits, block.timestamp)[3]);
    }

    function getAmount1InToken0(SwapParams memory swapParams, CalculateParams memory calculateParams) internal returns (uint256 amount1InToken0) {
        amount1InToken0 = (calculateParams.amount0Total * calculateParams.reserve1)
                / (calculateParams.reserve0 * calculateParams.denominator1 / calculateParams.denominator0 + calculateParams.reserve1);
        for (uint i = 0; i < calculateParams.precision; i++) {
            swapParams.amount = amount1InToken0;
            uint256 amount1 = queryBatchSwap(swapParams);
            amount1InToken0 = (calculateParams.amount0Total * calculateParams.reserve1)
                    / (calculateParams.reserve0 * amount1 / amount1InToken0 + calculateParams.reserve1);
        }
    }

    function getAmountLpTokens(SwapParams memory swapParams, CalculateParams memory calculateParams) internal returns (uint256 lpBalance) {
        lpBalance = (calculateParams.totalLpBalance * calculateParams.amount0Total)
                / (calculateParams.reserve0 + calculateParams.reserve1 * calculateParams.denominator0 / calculateParams.denominator1);
        for (uint i = 0; i < calculateParams.precision; i++) {
            uint256 amount1 = calculateParams.reserve1 * lpBalance / calculateParams.totalLpBalance;
            swapParams.amount = amount1;
            uint256 amount0 = queryBatchSwap(swapParams);
            lpBalance = (calculateParams.totalLpBalance * calculateParams.amount0Total)
                    / (calculateParams.reserve0 + calculateParams.reserve1 * amount0 / amount1);
        }
    }
}


